import logo from '../images/logo.png';
//import './App.css';
import React, {useState} from "react";
import { ButtonGroup, Button, Row, Container, Col} from 'react-bootstrap';
import axios from 'axios'
import {Link} from 'react-router-dom'

const base_url = 'http://localhost3001/'           //////change this

export class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userID : '',
            password : '',
            homeAirport : '',
            isRegistered : false,
        }
    }

    changeHandler = (e) => {
        e.preventDefault()
        this.setState({
            [e.target.name] : e.target.value,
        })
    }

    register = (userID, password, homeAirport) => {
        axios.post(base_url + 'users/register/create', {                              //// change this
          'user': {
              'userID' : userID,
              'password': password,
              'homeAirport': homeAirport
          }
        }).then((response)=> {
          console.log(response);
          this.setState({
            isRegistered: true
          });
        });
    };

    render() {
        const isRegistered =  this.state.isRegistered;
        return (
            <div className = "register">
              <div className = "registerinput">
                <img src={logo}></img>
                <h1> Not yet registered? </h1>
                <label> UserID</label>
                <input //userid input
                  type = "text" 
                  name = "userID"
                  value = {this.state.userID}
                  placeholder = "userID"
                  onChange={this.changeHandler}
                />

                <label> Password</label>
                <input //password input 
                  type = "text"
                  name = "password"
                  value = {this.state.password}
                  placeholder = "password"
                  onChange={this.changeHandler}
                />

                <label> Home Airport</label>
                <input //home airport input 
                  type = "text"
                  name = "homeAirport"
                  value = {this.state.homeAirport}
                  placeholder = "homeAirport"
                  onChange={this.changeHandler}
                />
              </div>
              <Link to='login'>
                <Button variant="outline-primary" onClick = {this.register}>Register</Button>
              </Link>

            </div>
          );
    }
}
