import logo from '../images/logo.png';
//import './App.css';
import React, {useState} from "react";
import { ButtonGroup, Button, Row, Container, Col} from 'react-bootstrap';
import Axios from 'axios'
import {Link} from 'react-router-dom'

const base_url = 'http://localhost3001/'           //////change this

export class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userID : '',
            password : '',
            homeAirport : '',
            isLoggedIn : false,
        }
    }

    changeHandler = (e) => {
        e.preventDefault()
        this.setState({
            [e.target.name] : e.target.value,
        })
    }

    login = e => {
        e.preventDefault()
        const {userID, password} = this.state
        axios.post(base_url + 'users/login', {                              //// change this
            'user': {
                'userID' : userID,
                'password': password,
            }
        }).then((response)=> {
            if (response.data.message){
              setLoginStatus(response.data.message)
            } else {
              setLoginStatus(response.data[0].userID) //这里改成redirect到profile page & add cookies
            }
        });      
    }

    render() {
        const isRegistered =  this.state.isRegistered;
        return (
            <div className = "login">
              <div className = "logininput">
                <img src={logo}></img>
                <h1> Register or Log In to search for routes and hotels!</h1>
                <label> UserID</label>
                <input //userid input
                  type = "text" 
                  name = "userID"
                  value = {this.state.userID}
                  placeholder = "userID"
                  onChange={this.changeHandler}
                />

                <label> Password</label>
                <input //password input 
                  type = "text"
                  name = "password"
                  value = {this.state.password}
                  placeholder = "password"
                  onChange={this.changeHandler}
                />

              </div>
              <Link to='register'>
                <Button variant="outline-primary">Register Instead</Button>
              </Link>
                <Button variant="outline-primary" onClick = {this.login}>Log In</Button>


              <h1>{loginStatus}</h1>
            </div>
          );
    }
}
